mapper.select:
SELECT no,poster,name,car_class,seat,fuel,premium_ins_price,num FROM (SELECT no,poster,name,car_class,seat,fuel,premium_ins_price,rownum as num FROM (SELECT no,poster,name,car_class,seat,fuel,premium_ins_price FROM car
WHERE car_class LIKE '%'||#{tabVal}||'%'
ORDER BY no ASC)) WHERE num BETWEEN #{start} AND #{end}

SELECT no,poster,name,car_class,seat,fuel,premium_ins_price,NON_INS_QUAL,num FROM (SELECT no,poster,name,car_class,seat,fuel,premium_ins_price,NON_INS_QUAL,rownum as num FROM (SELECT DISTINCT c.no,poster,name,car_class,seat,fuel,premium_ins_price,NON_INS_QUAL FROM car c LEFT OUTER JOIN car_reserve cr ON c.no = cr.car_no WHERE (pickup_date is null OR cr.car_no NOT IN( SELECT car_no FROM car_reserve WHERE( (pickup_date BETWEEN #{pickup_date} AND #{return_date}) OR (return_date BETWEEN #{pickup_date} AND #{return_date}) OR (#{pickup_date} BETWEEN pickup_date AND return_date) OR (#{return_date} BETWEEN pickup_date AND return_date) ) ))
AND (
car_class LIKE '%'||#{class}||'%'
)
AND (
fuel LIKE '%'||#{fuel}||'%'
)
ORDER BY c.no ASC)) WHERE num BETWEEN #{start} AND #{end}

SELECT CEIL(COUNT(*)/9.0) FROM(SELECT DISTINCT c.no FROM car c LEFT OUTER JOIN car_reserve cr ON c.no = cr.car_no WHERE (pickup_date is null OR cr.car_no NOT IN( SELECT car_no FROM car_reserve WHERE( (pickup_date BETWEEN #{pickup_date} AND #{return_date}) OR (return_date BETWEEN #{pickup_date} AND #{return_date}) OR (#{pickup_date} BETWEEN pickup_date AND return_date) OR (#{pickup_date} BETWEEN pickup_date AND return_date) ) ))
AND (
car_class LIKE '%'||#{class}||'%'
)
AND (
fuel LIKE '%'||#{fuel}||'%'
)
)
