mapper.select:
SELECT * FROM (SELECT hotl.*, ROWNUM AS num FROM (SELECT * FROM (SELECT h.no, h.title, h.img, h.addr, h.areacode, h.sigungucode, h.content_id, h.cat3, hr.price, hr.person, hr.person_max, hi.parking, hi.sauna, hi.sports, hi.checkintime, hi.checkouttime, ROW_NUMBER() OVER (PARTITION BY h.no ORDER BY hr.price ASC) AS rn FROM hotel h JOIN hotel_room hr ON hr.hotel_no = h.no JOIN hotel_info hi ON hi.hotel_no = h.no WHERE 1 = 1
AND hr.person <= #{person} AND hr.person_max >= #{person}
AND parking = 1
AND hi.sauna = 1
AND hi.sports = 1
AND hi.food_place = 1
AND hi.beverage = 1
AND hi.barbecue = 1
AND hi.bycle = 1
AND hi.publicpc = 1
AND hi.publicbath = 1
AND hi.seminar = 1
) rp WHERE rp.rn = 1
AND areacode = #{areacode}
AND sigungucode = #{sigungucode}
AND cat3 = #{cat3}
ORDER BY rp.price ASC ) hotl WHERE ROWNUM <= #{end}) WHERE num >= #{start}

    SELECT CEIL(COUNT(*)/8.0) 
    FROM (SELECT DISTINCT h.no 
    FROM hotel h 
    JOIN hotel_room hr ON hr.hotel_no = h.no
    JOIN hotel_info hi ON hi.hotel_no = h.no  


	    AND hr.person <= #{person}
	    AND hr.person_max >= #{person}


	    AND areacode = #{areacode} 


	    AND sigungucode = #{sigungucode} 


	    AND cat3 = #{cat3}


	    AND parking = 1


	    AND hi.sauna = 1


	    AND hi.sports = 1


	    AND hi.beverage = 1


	    AND hi.barbecue = 1


	    AND hi.bycle = 1


	    AND hi.publicpc = 1


	    AND hi.publicbath = 1


	    AND hi.seminar = 1


    )
