mapper.select:
SELECT * FROM (SELECT hotl.*, ROWNUM AS num FROM (SELECT * FROM (SELECT h.no, h.title, h.img, h.addr, h.areacode, h.sigungucode, h.content_id,h.cat3, hr.price, hr.person, hr.person_max, ROW_NUMBER() OVER (PARTITION BY h.no ORDER BY hr.price ASC) AS rn FROM hotel h JOIN hotel_room hr ON hr.hotel_no = h.no) rp WHERE rp.rn = 1
AND h.areacode = #{areacode}
ORDER BY rp.price DESC) hotl
ORDER BY rp.price ASC) hotl
WHERE ROWNUM <= #{end}) WHERE ROWNUM >= #{start}
